import React, { useEffect, useState, useContext } from 'react';
import { UserContext } from '../App';
const Profile = () => {
    const[pics,setPics]=useState([]);
    const{state,dispatch}=useContext(UserContext)
    useEffect(()=>{
          fetch("/mypost",{
              headers:{
                  "Authorization" :"Bearer "+localStorage.getItem("jwt")
              }
          }).then(res=>res.json()).then((results=>{
              setPics(results.mypost)
          }))
    },[])
    return ( 
      <div style={{maxWidth:"700px",margin:"0px auto"}}>
          <div style={{display:"flex",margin:"18px 0px",borderBottom:"1px solid black",justifyContent:"space-around"}}>
            
              <div style={{backgroundImage:"https://images.unsplash.com/photo-1533134486753-c833f0ed4866?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"}}>
                

              </div>
              <div style={{}}>
                  <h4 style={{marginLeft:"70px"}}>{(state)?state.name:"loading"}</h4>
                  <div style={{display:"flex",width:"108%",justifyContent:"space-between"}}>
                      <h6 style={{}}>10 posts</h6>
                      <h6>10 following</h6>
                      <h6>10 follower</h6>
                  </div>
              </div>
          </div>
     
      <div className="postss" style={{display:"flex",flexWrap:"wrap",justifyContent:"space-around"}}></div>
      {
          pics.map(x=>{
              return(
                <img style={{width:"30%" ,marginLeft:"3px"}} src={x.photo} alt={x.title} />
              )
          })
      }
     
       </div>
      

     );
}
 
export default Profile;
