import React,{useState,useEffect,useContext} from 'react';
import { UserContext } from '../App';
const Home = () => {
    const [data,setData]=useState([]);
    const {state,dispatch}=useContext(UserContext)
    useEffect(()=>{
        fetch('/getposts',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json()).then(result=>{
            setData(result.posts)
        })
    },[])
    const likepost=(id)=>{
        fetch('/like',{
            method :"put",
            headers :{
                "Content-Type" :"application/json",
                "Authorization" :"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId : id,
            })
        }).then(res=>res.json()).then(result=>{
            const newData = data.map(item=>{
                if(item._id==result._id)
                return result
                else 
                return item
            })
            setData(newData)
        }).catch(err=>{
            console.log(err)
        })
    }
    const unlikepost=(id)=>{
        fetch('/unlike',{
            method :"put",
            headers :{
                "Content-Type" :"application/json",
                "Authorization" :"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId : id,
            })
        }).then(res=>res.json()).then(result=>{
            const newData = data.map(item=>{
                if(item._id==result._id)
                return result
                else 
                return item
            })
            setData(newData)
        
        }).catch(err=>{
            console.log(err)
        })
    }
    return ( 
        <div className="homepage" >
            {
                data.map(x=>{
                    return(
                        <div className="card" key={x.id} style={{
                            maxWidth:"600px",
                            height:"max-content",
                            margin:"15px auto"
                        }}>
                            <h4>{x.postedBy.name}</h4>
                            <div className="card-image">
                                <img src={x.photo} />
                            </div>
                            <div className="card-content">
                                    <i className="material-icons" onClick={()=>{likepost(x._id)}}>thumb_up</i>
                                    <i className="material-icons" onClick={()=>{unlikepost(x._id)}}>thumb_down</i>
                                    <h6>{x.likes.length} likes</h6>
                                     <h6>{x.title}</h6>
                                     <p>{x.body}</p>
                                     <input type="text" placeholder="add a comment" />
                            </div>
                             
                        </div>
                    )
                })
            }
         

        </div>
     );
}
 
export default Home;
