import React,{useState,useEffect} from 'react';
const Home = () => {
    const [data,setData]=useState([]);
    useEffect(()=>{
        fetch('/getposts',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json()).then(result=>{
            setData(result.posts)
        })
    },[])
    const likepost=(id)=>{
        fetch("/like",{
            method :"put",
            headers :{
                "Content-Type" :"application/json",
                "Authorization" :"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId : id,
            })
        }).then(res=>res.json()).then(result=>{
            console.log(result)
        })
    }
    return ( 
        <div className="homepage" >
            {
                data.map(x=>{
                    return(
                        <div className="card" key={x.id} style={{
                            maxWidth:"600px",
                            height:"max-content",
                            margin:"15px auto"
                        }}>
                            <h4>{x.postedBy.name}</h4>
                            <div className="card-image">
                                <img src={x.photo} />
                            </div>
                            <div className="card-content">
                                    <i class="material-icons" onClick={()=>{likepost(x.id)}}>thumb_up</i>
                                    <i class="material-icons">thumb_down</i>
                                    <h6>{x.likes.length} likes</h6>
                                     <h6>{x.title}</h6>
                                     <p>{x.body}</p>
                                     <input type="text" placeholder="add a comment" />
                            </div>
                             
                        </div>
                    )
                })
            }
         

        </div>
     );
}
 
export default Home;
