import React,{useState,useEffect} from 'react';
const Home = () => {
    const [data,setData]=useState([]);
    useEffect(()=>{
        fetch('/getposts',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json()).then(result=>{
            setData(result.post)
        })
    },[])
    return ( 
        <div className="homepage" >
            {
                data.map(x=>{
                    return(
                        <div className="card" style={{
                            maxWidth:"600px",
                            height:"max-content",
                            margin:"15px auto"
                        }}>
                            <h4>Joker</h4>
                            <div className="card-image">
                                <img src="https://images.unsplash.com/photo-1542300058-849d3b08aa0f?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" />
                            </div>
                            <div className="card-content">
                                     <h6>title </h6>
                                     <p>this is amazing</p>
                                     <input type="text" placeholder="add a comment" />
                            </div>
                             
                        </div>
                    )
                })
            }
         

        </div>
     );
}
 
export default Home;
